mapper.select:
select * from t_cfg_user

    id = #{id}
    and user_name = #{userName}
    and phone = #{phone}
    and email = #{email}

limit 1

select count(1) from t_cfg_user

    id = #{id}
    and customer_id = #{customerId}
    and user_name = #{userName}

mapper.delete:
delete from t_cfg_user

    id = #{id}
    and customer_id = #{customerId}

mapper.update:
update t_cfg_user
customer_id = #{customerId}, user_name = #{userName}, password = #{password}, true_name = #{trueName}, phone = #{phone}, user_type = #{userType}, email = #{email}, update_time = now()
id = #{id} and user_name = #{userName}
